Vue.directive('focus', function(el, bd){
    //文本框聚焦
    if(bd.value && !el.value)el.focus();
});

var vm = new Vue({
    el: '#address_at',
    data: {
        searchIpt: '',
        isToInput: false,
        people: [],
        peoplePick: [],
        peoplePickName: [],
        peopleLen: 0,
        fastFindText: '',
        indexElement: null,
        title: '',
        isShowPicked: false
    },
    methods: {
        searchToInput: function(){
            //从文本标签切换到输入框
            this.isToInput = true;
        },
        searchToText: function(){
            //从输入框切换到文本标签
            if(this.searchIpt != '') return false;
            this.isToInput = false;
        },
        searchEmpty: function(){
            //清除输入框
            this.searchIpt = '';
            this.isToInput = false;
            this.searchFilter();
        },
        headpicReplace: function(vals){
            //加载头像失败, 替换成文字图像
            //vals.hashead = false;
        },
        fastFind: function(){
            //滑动右侧序列快速分类找
            var point = event.changedTouches ? event.changedTouches[0] : event,
                pointElement = document.elementFromPoint(point.pageX, point.pageY),
                parentElement =  document.querySelectorAll('.address-index-box')[0];
            
            //非当前项去除样式
            if(this.indexElement){
                this.indexElement.classList.remove('active');
                this.indexElement = null;
            }
            
            //根据触摸对应序列文字, 显示序列名
            if(pointElement && pointElement.parentNode==parentElement){
                var group = pointElement.innerText,
                    groupFull = pointElement.dataset.group;
                
                this.indexElement = pointElement;
                if(group && group.length<=2){
                    this.fastFindText = group;
                }
            }
            
            //定位
            var groupEl = document.querySelector('[data-group="' + groupFull + '"]');
            if(!groupEl) return;
            
            var platForm = appcan.locStorage.getVal("platForm");
            document.querySelector('.address-list').scrollTop = platForm=='1'?groupEl.offsetTop:groupEl.offsetTop-15;
            
            //样式改变
            pointElement.classList.add('active');
        },
        fastFindEnd: function(){
            //退出滑动序列
            this.fastFindText = '';
            if(this.indexElement){
                this.indexElement.classList.remove('active');
                this.indexElement = null;
            }
        },
        searchFilter: function(){
            var sipt = this.searchIpt.toLowerCase(),
                itemNone = document.querySelector('.nothing'),
                groupIdx = document.querySelectorAll('.address-index span'),
                groupArr = [].slice.call(document.querySelectorAll('.myli-box'));
            var countGroup = 0;
            
            groupArr.forEach(function(gp, gi){
                var itemArr = [].slice.call(gp.querySelectorAll('.myli>li'));
                var thisgroup = gp.dataset.group.trim(),
                    countItem = 0;
                itemArr.forEach(function(it){
                    var thisname = it.dataset.name.trim(),
                        thispinyin = it.dataset.pinyin.trim(),
                        thisszm = it.dataset.szm.trim();
                    
                    if(sipt != ''){
                        if(thisname.indexOf(sipt)<0 
                            && thispinyin.indexOf(sipt)<0
                            && thisszm.indexOf(sipt)<0){
                            it.classList.add('hide');
                            countItem++;
                        }else{
                            it.classList.remove('hide');
                        }
                    }else{
                        it.classList.remove('hide');
                    }
                });
                
                //一个单位里没有符合匹配项的人, 该单位整体隐藏
                if(sipt!='' && countItem==itemArr.length){
                    gp.classList.add('hide');
                    groupIdx[gi].classList.add('hide');
                    countGroup++;
                }else{
                    gp.classList.remove('hide');
                    groupIdx[gi].classList.remove('hide');
                }
            });
            
            if(sipt!='' && countGroup==groupArr.length){
                itemNone.classList.remove('hide');
            }else{
                itemNone.classList.add('hide');
            }
            
            document.querySelector('.address-list').scrollTop = 0;
        },
        pickPeople: function(ucode, flag){
            //flag: 该项是否已选过且不能再次选择, true为不可选
            var _this = this,
                headurl = null;
            
            //找出usercode对应的数据单元, 然后改变checked
            uno:
            for(var r=0; r<_this.people.length; r++){
                dos:
                for(var s=0; s<_this.people[r].jsonStr.length; s++){
                    _val = _this.people[r].jsonStr[s];
                    
                    if(_val.userCode == ucode){
                        _val.disabled = flag;
                        if(!flag){
                            _val.checked = !_val.checked;
                            if(_val.checked){
                                //若选中, 加入peoplePick数组
                                var pickThis = {};
                                pickThis.name = _val.userName;
                                pickThis.id = _val.id;
                                pickThis.userCode = _val.userCode;
                                pickThis.hashead = _val.hashead;
                                pickThis.headurl = _val.headurl;
                                pickThis.headtext = _val.headtext;
                                pickThis.headbgclass = _val.headbgclass;
                                
                                _this.peoplePick = _this.peoplePick.concat(pickThis);
                                //人名的数组
                                _this.peoplePickName = _this.peoplePickName.concat(_val.userName);
                            }else{
                                //若取消选中, 从peoplePick数组中移除
                                tres:
                                for(var t=0; t<_this.peoplePick.length; t++){
                                    if(_this.peoplePick[t].userCode == ucode){
                                        var ppn = $.inArray(_this.peoplePick[t].name, _this.peoplePickName);
                                        _this.peoplePickName.splice(ppn, 1);
                                        _this.peoplePick.splice(t, 1);
                                        break tres;
                                    }
                                }
                            }
                            break uno; //跳出最外层循环
                        }
                    }
                }//dos
            }//uno
            
        },
        showPicked: function(flag){
            this.isShowPicked = flag;
        }
    }
});
    
