//筛选群成员信息, 并计算总人数
function contactsHandle(data){
    for(var x=0; x<data.length; x++){
        for(var y=0; y<data[x].jsonStr.length; y++){
            if($.inArray(data[x].jsonStr[y].userCode, members)==-1){
                data[x].jsonStr.splice(y, 1);
                y--;
            }else{
                vm.peopleLen++;
            }
        }
    }
    vm.people = data;
}
